import React, { Component } from 'react';
import '../App.css'
//import './Product.css'
import toaster from "toasted-notes";



class ShoppingItem extends React.Component{
    constructor(props){
      super(props);
      this.state={
        clicks: 0,
        show: true,
      };
      this.MyFunction = this.myFunction.bind(this);
      
    }
    IncrementItem =()=>{
      this.setState({clicks:this.state.clicks +1});
      toaster.notify("Du har lagt till " + this.props.item.name + " i din varukorg", {
          duration: 2000
      })
    }
    DecreaseItem =()=>{
      this.setState({clicks:this.state.clicks -1});
      toaster.notify("Du har tagit bort " + this.props.item.name + " från din varukorg", {
        duration: 2000,
        appearance: 'success',
        autoDissmiss: true, 
        pauseOnHover: false,
      })
    }

    MyFunction() {
      // Get the snackbar DIV
      var x = document.getElementById("snackbar");
    
      // Add the "show" class to DIV
      x.className = "show";
    
      // After 3 seconds, remove the show class from DIV
      setTimeout(function(){ x.className = x.className.replace("show", ""); }, 3000);
    }

    ConfirmPurchase =()=>{
      //TODO
    }
    render(){
      return(
        <div className="ShoppingItem">
          <div className="Item">
            {this.props.item.name}
          </div>
          <div className="Price">
            {this.props.item.price} kr
          </div>
          <div className="Buttons">
            {this.state.show ? <h2>{this.state.clicks}</h2>:''}
            <button onClick={this.DecreaseItem, this.props.MyFunction()}>-</button>
            <button onClick={this.IncrementItem}>+</button>
            </div>
            <div id="snackbar">"Du har köpt en + {this.props.item.name}"
            </div>
            </div>
      );
    }
    
  }
  export default ShoppingItem;
